import "./App.css";
import "./index.css";
import { useReducer, useState, useEffect } from "react";
import greek_salad from "./images/greek_salad.png";
import bruchetta from "./images/bruchetta.png";
import lemon_dessert from "./images/lemon_dessert.jpg";
import Home from "./components/Home";
import { Route, Routes } from "react-router-dom";
import BookingPage from "./components/BookingPage";
import fetchAPI from "./mockAPI";

function App() {
  const [cards, setCard] = useState([
    {
      title: "Greek Salad",
      description:
        "The famous greek salad of crispy lettuce, peppers, olives and our Chicago style feta cheese gurnished with garlic",
      image: greek_salad,
    },
    {
      title: "Bruchetta",
      description:
        "Our Bruchetta made from grilled bread that has been smeared with garlic and seasoned with salt and olive oil ",
      image: bruchetta,
    },
    {
      title: "Lemon Dessert",
      description:
        "This comes straight from grandmas recipe book, every last ingredient has been sourced and is authentic as can be imagined",
      image: lemon_dessert,
    },
  ]);

  const sDate = new Date().toISOString().split("T")[0];
  fetchAPI(new Date(sDate))
    .then((response) => JSON.parse(response))
    .then((availableTimes) =>
      dispatch({ type: "initializeTimes", availableTimes: availableTimes })
    );

  const fetchData = () => {
    fetchAPI(new Date())().then((response) =>
      response.json().then((data) => {
        initializeTimes(data);
      })
    );
  };

  useEffect(() => {
    fetchData();
  }, []);

  const initializeTimes = (data) => {
    return data;
  };

  const updateTimes = (availableTimes, action) => {
    console.log("value a gave from use effect", action.type);
    if (action.type === "reserved") {
      console.log("action.value", action.value);
      console.log("state is", availableTimes);
      return availableTimes.filter((time) => time.value !== action.value);
    }
    throw Error("Unknown action: " + action.type);
  };

  const [availableTimes, dispatch] = useReducer(updateTimes, initializeTimes());

  return (
    <div className="box">
      <Routes>
        <Route path="/" element={<Home cards={cards} />} />
        <Route
          path="/reservations"
          element={
            <BookingPage availableTimes={availableTimes} dispatch={dispatch} />
          }
        ></Route>
      </Routes>
    </div>
  );
}

export default App;
