import React from "react";
import { useState } from "react";
import "../css/bookingForm.css";
import Button from "./Button";

const BookingForm = () => {
  let errors = {};
  const [bookingData, setBookingData] = useState({
    date: new Date(),
    time: "",
    guests: "",
    occasion: "",
  });

  const occasions = [
    { value: "Birthday", text: "Birthday" },
    { value: "Anniversary", text: "Anniversary" },
  ];

  const times = [
    { value: "17-00", text: "17-00" },
    { value: "18-00", text: "18-00" },
    { value: "19-00", text: "19-00" },
    { value: "20-00", text: "20-00" },
    { value: "21-00", text: "21-00" },
    { value: "22-00", text: "22-00" },
  ];

  const handleSubmit = (e) => {
    let formIsValid = false;
    e.preventDefault();
    console.log(bookingData);
    if (!bookingData.time) {
      errors.time = "Please choose a time";
    }
    if (!bookingData.guests) {
      errors.time = "Please choose a number of guests";
    }
    if (!bookingData.occasion) {
      errors.time = "Please choose an occasion";
    }
  };
  return (
    <>
      <div className="reservation-box">
        <form onSubmit={handleSubmit} className="booking-form">
          <label htmlFor="res-date" className="booking-label">
            Choose date:{" "}
          </label>
          <input
            className="form-control"
            type="date"
            placeholder="Choose the date"
            id="res-date"
            name="res-date"
            value={bookingData.date}
            onChange={(e) =>
              setBookingData({ ...bookingData, date: e.target.value })
            }
          />
          <div className="group-label-input">
            <label htmlFor="res-time" className="booking-label">
              Choose time:{" "}
            </label>

            <select
              type="button"
              id="res-time"
              name="res-time"
              value={bookingData.time}
              className={errors.title ? "red-border" : "form-control"}
              onChange={(e) =>
                setBookingData({ ...bookingData, time: e.target.value })
              }
            >
              {times.map((option) => (
                <option key={option.value} value={option.value}>
                  {option.text}
                </option>
              ))}
            </select>
          </div>
          <label htmlFor="guests" className="booking-label">
            Number of guests:{" "}
          </label>
          <input
            className="form-control"
            type="number"
            placeholder="1"
            min="1"
            max="10"
            id="guests"
            name="guests"
            value={bookingData.guests}
            onChange={(e) =>
              setBookingData({ ...bookingData, guests: e.target.value })
            }
          />
          <label htmlFor="occasion" className="booking-label">
            Occasion
          </label>

          <select
            className="form-control"
            type="button"
            id="occasion"
            value={bookingData.occasion}
            onChange={(e) =>
              setBookingData({ ...bookingData, occasion: e.target.value })
            }
          >
            {occasions.map((option) => (
              <option key={option.value} value={option.value}>
                {option.text}
              </option>
            ))}
          </select>
          <Button type="submit" text="Reserve a table" />
        </form>
      </div>
    </>
  );
};

export default BookingForm;
