import React from "react";
import { useState, useEffect } from "react";
import "../css/bookingForm.css";
import Button from "./Button";

const BookingForm = ({ availableTimes, setAvailableTimes }) => {
  const [bookingData, setBookingData] = useState({
    date: new Date(),
    time: "",
    guests: "",
    occasion: "",
  });

  const occasions = [
    { value: "Birthday", text: "Birthday" },
    { value: "Anniversary", text: "Anniversary" },
  ];

  const [errors, setErrors] = useState({});

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(bookingData);
    if (!bookingData.time) {
      setErrors((prevState) => ({
        ...prevState,
        time: "Please choose a time",
      }));
    }

    if (!bookingData.guests) {
      setErrors((prevState) => ({
        ...prevState,
        guests: "Please choose a number of guests",
      }));
    }
    if (!bookingData.occasion) {
      setErrors((prevState) => ({
        ...prevState,
        occasion: "Please choose an occasion",
      }));
    }
    console.log("Errors", errors);
  };

  useEffect(() => {
    errors.clear();
  }, [errors, bookingData]);

  return (
    <>
      <div className="reservation-box">
        <form onSubmit={handleSubmit} className="booking-form">
          <label htmlFor="res-date" className="booking-label">
            Choose date:{" "}
          </label>
          <input
            required
            className="form-control"
            type="date"
            placeholder="Choose the date"
            id="res-date"
            name="res-date"
            value={bookingData.date}
            onChange={(e) =>
              setBookingData({ ...bookingData, date: e.target.value })
            }
          />
          <div className="group-label-input">
            <label htmlFor="res-time" className="booking-label">
              {errors.time ? (
                <span style={{ color: "red", fontSize: "15px" }}>
                  {errors.time}
                </span>
              ) : (
                "Choose time"
              )}
            </label>

            <select
              type="button"
              id="res-time"
              name="res-time"
              value={bookingData.time}
              className={
                errors.time ? "form-control is-invalid" : "form-control"
              }
              onChange={(e) =>
                setBookingData({ ...bookingData, time: e.target.value })
              }
            >
              {availableTimes.map((option) => (
                <option key={option.value} value={option.value}>
                  {option.text}
                </option>
              ))}
            </select>
          </div>
          <label htmlFor="guests" className="booking-label">
            {errors.guests ? (
              <span style={{ color: "red", fontSize: "15px" }}>
                {errors.guests}
              </span>
            ) : (
              "Number of guests"
            )}
          </label>
          <input
            className={
              errors.guests ? "form-control is-invalid" : "form-control"
            }
            type="number"
            placeholder="1"
            min="1"
            max="10"
            id="guests"
            name="guests"
            value={bookingData.guests}
            onChange={(e) =>
              setBookingData({ ...bookingData, guests: e.target.value })
            }
          />
          <label htmlFor="occasion" className="booking-label">
            {errors.occasion ? (
              <span style={{ color: "red", fontSize: "15px" }}>
                {errors.occasion}
              </span>
            ) : (
              "Occasion"
            )}
          </label>

          <select
            className={
              errors.occasion ? "form-control is-invalid" : "form-control"
            }
            type="button"
            id="occasion"
            value={bookingData.occasion}
            onChange={(e) =>
              setBookingData({ ...bookingData, occasion: e.target.value })
            }
          >
            {errors.occasion}
            {occasions.map((option) => (
              <option key={option.value} value={option.value}>
                {option.text}
              </option>
            ))}
          </select>
          <Button type="submit" text="Reserve a table" />
        </form>
      </div>
    </>
  );
};

export default BookingForm;
