import React from "react";
import { useState } from "react";

const BookingForm = () => {
  const [bookingData, setBookingData] = useState({
    date: new Date(),
    time: "",
    guests: "",
    occasion: "",
  });

  const occasions = [
    { value: "birthday", text: "birthday" },
    { value: "anniversary", text: "anniversary" },
  ];

  const handleChange = (event) => {
    console.log("I am in the handle change", event.target.value);
    setBookingData({ ...bookingData, occasion: event.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(bookingData);
  };
  return (
    <>
      <form onSubmit={handleSubmit} className="booking-form">
        <label htmlFor="res-date">Choose date: </label>
        <input
          type="date"
          placeholder="Choose the date"
          id="res-date"
          name="res-date"
          value={bookingData.date}
          onChange={(e) =>
            setBookingData({ ...bookingData, date: e.target.value })
          }
        />
        <label htmlFor="res-time">Choose time: </label>

        <select id="res-time " value={bookingData.time}>
          <option
            value="17-00"
            onChange={(e) => {
              console.log("I am here!", e.target.value);
              setBookingData({ ...bookingData, time: e.target.value });
            }}
          >
            17:00
          </option>
          <option
            value="18-00"
            onChange={(e) =>
              setBookingData({ ...bookingData, time: e.target.value })
            }
          >
            18:00
          </option>
          <option
            value="19-00"
            onChange={(e) =>
              setBookingData({ ...bookingData, time: e.target.value })
            }
          >
            19:00
          </option>
          <option
            value="20-00"
            onChange={(e) =>
              setBookingData({ ...bookingData, time: e.target.value })
            }
          >
            20:00
          </option>
          <option
            value="21-00"
            onChange={(e) =>
              setBookingData({ ...bookingData, time: e.target.value })
            }
          >
            21:00
          </option>
          <option
            value="22-00"
            onChange={(e) =>
              setBookingData({ ...bookingData, time: e.target.value })
            }
          >
            22:00
          </option>
        </select>
        <label htmlFor="guests">Number of guests: </label>
        <input
          type="number"
          placeholder="1"
          min="1"
          max="10"
          id="guests"
          name="guests"
          value={bookingData.guests}
          onChange={(e) =>
            setBookingData({ ...bookingData, guests: e.target.value })
          }
        />
        <label htmlFor="occasion">Occasion</label>
        <select
          id="occasion"
          value={bookingData.occasion}
          onChange={handleChange}
        >
          <option
            value="birthday"
            onChange={(e) =>
              setBookingData({ ...bookingData, occasion: e.target.value })
            }
          >
            Birthday
          </option>
          <option
            value="anniversary"
            onChange={(e) =>
              setBookingData({ ...bookingData, occasion: e.target.value })
            }
          >
            Anniversary
          </option>
        </select>
        <input type="submit" value="Make Your reservation" />
      </form>
    </>
  );
};

export default BookingForm;
