import React from "react";
import BookingForm from "./BookingFrom";
import Footer from "./Footer";
import Hero from "./Hero";
import Nav from "./Nav";
import "../css/bookingForm.css";
import "../css/main.css";
import { fetchAPI } from "../mockAPI";
import { useReducer, useState } from "react";

const BookingPage = () => {
  const [isFormSubmitted, setIsFormSubmitted] = useState(false);

  const handleFormSubmit = (e, formValues) => {
    e.preventDefault();
    setIsFormSubmitted(true);
    const areAllFieldsFilled = Object.values(formValues).every(
      (value) => value
    );
  };

  const initializeTimes = (initialAvailableTimes) => [...fetchAPI(new Date())];

  const updateTimes = (availableTimes, action) => {
    if (action.type === "reserved") {
      const [selectedDate, selectedTime] = action.value;
      const times = fetchAPI(new Date(selectedDate));
      return availableTimes.filter((time) => time !== selectedTime);
    }
    throw Error("Unknown action: " + action.type);
  };

  const [availableTimes, dispatch] = useReducer[(updateTimes, initializeTimes)];
  return (
    <>
      <Nav />
      <div className="main">
        <Hero />
        <BookingForm
          availableTimes={availableTimes}
          dispatch={dispatch}
          onFormSubmit={handleFormSubmit}
          isFormSubmitted={isFormSubmitted}
        />
      </div>
      <Footer />
    </>
  );
};

export default BookingPage;
