import "./App.css";
import "./index.css";
import { useReducer, useState } from "react";
import greek_salad from "./images/greek_salad.png";
import bruchetta from "./images/bruchetta.png";
import lemon_dessert from "./images/lemon_dessert.jpg";
import Home from "./components/Home";
import { Route, Routes } from "react-router-dom";
import Nav from "./components/Nav.js";
import Main from "./components/Main.js";
import Footer from "./components/Footer.js";
import BookingPage from "./components/BookingPage";

function App() {
  const [cards, setCard] = useState([
    {
      title: "Greek Salad",
      description:
        "The famous greek salad of crispy lettuce, peppers, olives and our Chicago style feta cheese gurnished with garlic",
      image: greek_salad,
    },
    {
      title: "Bruchetta",
      description:
        "Our Bruchetta made from grilled bread that has been smeared with garlic and seasoned with salt and olive oil ",
      image: bruchetta,
    },
    {
      title: "Lemon Dessert",
      description:
        "This comes straight from grandmas recipe book, every last ingredient has been sourced and is authentic as can be imagined",
      image: lemon_dessert,
    },
  ]);
  const initialTimes = [
    { value: "Choose time", text: "Choose time" },
    { value: "17-00", text: "17-00" },
    { value: "18-00", text: "18-00" },
    { value: "19-00", text: "19-00" },
    { value: "20-00", text: "20-00" },
    { value: "21-00", text: "21-00" },
    { value: "22-00", text: "22-00" },
  ];

  const reducer = (state, action) => {
    console.log("I am at DISPATCH");
    console.log("value a gave from use effect", action.type);
    switch (action.type) {
      case "reserved":
        console.log("action.value", action.value);
        console.log("state is", state);
        return state.filter((time) => time.value !== action.value);
      default: {
        throw Error("Unknown action: " + action.type);
      }
    }
  };

  const [state, dispatch] = useReducer(reducer, initialTimes);

  return (
    <div className="box">
      <Routes>
        <Route path="/" element={<Home cards={cards} />} />
        <Route
          path="/reservations"
          element={<BookingPage state={state} dispatch={dispatch} />}
        ></Route>
      </Routes>
    </div>
  );
}

export default App;
