import React from "react";
import { useState } from "react";
import { RadioGroup, RadioOption } from "./RadioOption";

const BookingForm = () => {
  const [bookingData, setBookingData] = useState({});
  const [selectedTime, setSelectedTime] = useState("");
  const [selectedOccasion, setSelectedOccasion] = useState("");
  const handleSubmit = () => {};
  return (
    <>
      <form onSubmit={handleSubmit} className="booking-form">
        <label htmlFor="res-date">Choose date: </label>
        <input
          type="date"
          placeholder="Choose the date"
          id="res-date"
          name="res-date"
          value={bookingData.date}
          onChange={(e) =>
            setBookingData({ ...bookingData, date: e.target.value })
          }
        />
        <label htmlFor="res-time">Choose time: </label>

        <RadioGroup onChange={setSelectedTime} selected={selectedTime}>
          <RadioOption value="17-00">17-00</RadioOption>
          <RadioOption value="18-00">18-00</RadioOption>
          <RadioOption value="19-00">19-00</RadioOption>
          <RadioOption value="20-00">20-00</RadioOption>
          <RadioOption value="21-00">21-00</RadioOption>
        </RadioGroup>
        <label htmlFor="guests">Number of guests: </label>
        <input
          type="number"
          placeholder="1"
          min="1"
          max="10"
          id="guests"
          name="guests"
          value={bookingData.guests}
          onChange={(e) =>
            setBookingData({ ...bookingData, guests: e.target.value })
          }
        />
        <label
          htmlFor="occasion"
          onChange={setSelectedOccasion}
          selected={selectedOccasion}
        >
          Occasion
        </label>
        <select id="occasion">
          <option value="birthday">Birthday</option>
          <option value="anniversary">Anniversary</option>
        </select>
        <input type="submit" value="Make Your reservation" />
      </form>
    </>
  );
};

export default BookingForm;
